import React, { useState } from 'react';

function FilterableProductTable({ products }) {
  const [filterText, setFilterText] = useState('');
  const [inStockOnly, setInStockOnly] = useState(false);

  return (
    <div>
      <SearchBar 
        filterText={filterText} 
        inStockOnly={inStockOnly} 
        onFilterTextChange={setFilterText} 
        onInStockOnlyChange={setInStockOnly} />
      <ProductTable 
        products={products} 
        filterText={filterText}
        inStockOnly={inStockOnly} />
    </div>
  );
}

function ProductCategoryRow({ category }) {
  return (
    <tr>
      <th colSpan="3">
        {category}
      </th>
    </tr>
  );
}

function ProductRow({ product, isSelected, onToggleSelect }) {
  const name = product.stocked ? product.name : <span style={{ color: 'red' }}>{product.name}</span>;

  const handleClick = () => {
    onToggleSelect(product.id);
  };

  return (
    <tr onClick={handleClick}>
      <td>{name}</td>
      <td>{product.price}</td>
      <td><input type="checkbox" checked={isSelected} readOnly /></td>
    </tr>
  );
}

function ProductTable({ products, filterText, inStockOnly, selectedRows, onToggleSelect }) {
  const rows = [];
  let lastCategory = null;

  products.forEach((product) => {
    if (
      product.name.toLowerCase().indexOf(
        filterText.toLowerCase()
      ) === -1
    ) {
      return;
    }
    if (inStockOnly && !product.stocked) {
      return;
    }
    if (product.category !== lastCategory) {
      rows.push(
        <ProductCategoryRow
          category={product.category}
          key={product.category} />
      );
    }
    const isSelected = selectedRows.includes(product.id);
    rows.push(
      <ProductRow
        product={product}
        isSelected={isSelected}
        onToggleSelect={onToggleSelect}
        key={product.id} />
    );
    lastCategory = product.category;
  });

  return (
    <table>
      <thead>
        <tr>
          <th>Nombre</th>
          <th>Precio</th>
          <th>Seleccionar</th>
        </tr>
      </thead>
      <tbody>{rows}</tbody>
    </table>
  );
}

function SearchBar({
  filterText,
  inStockOnly,
  onFilterTextChange,
  onInStockOnlyChange
}) {
  return (
    <form>
      <input 
        type="text" 
        value={filterText} placeholder="Buscar..." 
        onChange={(e) => onFilterTextChange(e.target.value)} />
      <label>
        <input 
          type="checkbox" 
          checked={inStockOnly} 
          onChange={(e) => onInStockOnlyChange(e.target.checked)} />
        {' '}
        Mostrar solo productos en stock
      </label>
    </form>
  );
}

const PRODUCTS = [
  {category: "Frutas", price: "$1", stocked: true, name: "Manzana", id: 1},
  {category: "Frutas", price: "$1", stocked: true, name: "Fruta del dragón", id: 2},
  {category: "Frutas", price: "$2", stocked: false, name: "Maracuyá", id: 3},
  {category: "Verduras", price: "$2", stocked: true, name: "Espinaca", id: 4},
  {category: "Verduras", price: "$4", stocked: false, name: "Calabaza", id: 5},
  {category: "Verduras", price: "$1", stocked: true, name: "Guisantes", id: 6}
];

export default function App() {
  const [selectedRows, setSelectedRows] = useState([]);

  const handleToggleSelect = (productId) => {
    if (selectedRows.includes(productId)) {
      setSelectedRows(selectedRows.filter(id => id !== productId));
    } else {
      setSelectedRows([...selectedRows, productId]);
    }
  };

  const handleDeleteSelected = () => {
    let isDelete = window.confirm(`Estás seguro de borrar los productos seleccionados?`);
    if (isDelete) {
      const newProducts = PRODUCTS.filter(product =>
        !selectedRows.includes(product.id)
      );
      setSelectedRows([]);
      console.log(newProducts);
    }
  };

  return (
    <div>
      <FilterableProductTable products={PRODUCTS} />
      <button onClick={handleDeleteSelected}>Borrar seleccionados</button>
      <ProductTable
        products={PRODUCTS}
        selectedRows={selectedRows}
        onToggleSelect={handleToggleSelect}
      />
    </div>
  );
}
