import React, { useEffect, useState } from 'react'
import { Formulario } from './components/Formulario'
import { v4 as uuidv4 } from 'uuid';
import { Tabla, TableRow } from './components/TableRow';
uuidv4();

export const App = () => {

  const [cosas, setCosas] = useState([])
  const [storage, setStorage] = useState("")
  useEffect(() => {
      let data = JSON.parse(localStorage.getItem('dato'))
      setCosas(data)
  },[])
  const addItem = (item) => {
    setCosas([...cosas, item])
    setStorage(JSON.stringify(cosas))
  }
  const updateStorage = () =>{
    localStorage.setItem('dato', storage)
  }
  const deleteItem = (id) => {
    setCosas(cosas.filter(item => item.id !== id))
    setStorage(JSON.stringify)
  } 
  return (
    <div>
      <h1>LISTA COMPRA</h1>
      <Formulario addItem={addItem} updateStorage={updateStorage} />
      <table>
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Cantidad</th>
            <th>Categor√≠a</th>
          </tr>
        </thead>
        <tbody>
        {
        cosas.map(item => (
          <TableRow item={item} deleteItem={deleteItem} />
        ))
      }
        </tbody>
      </table>

    </div>
  )
}
