import React, { useEffect, useState } from 'react'
import { Formulario } from './components/Formulario'
import { v4 as uuidv4 } from 'uuid';
import { Tabla, TableRow } from './components/TableRow';
uuidv4();

export const App = () => {


  const [cosas, setCosas] = useState([])
  const [cosasupdt, setCosasupdt] = useState([])
  //almacenar datos

  //leer datos
  useEffect(() => {
    console.log("SE HA RECARGADO 1")
    let datos = localStorage.getItem('datos');
    if(datos){
      console.log("HAY DATOS")
      console.log(datos)
    }else{
      console.log("NO HABIAN DATOS")
            const products = [
        { id:uuidv4(), category: "Frutas", cantidad: "1", stocked: true, name: "Manzana" },
        { id:uuidv4(), category: "Frutas", cantidad: "1", stocked: true, name: "Fruta del dragón" },
        { id:uuidv4(), category: "Frutas", cantidad: "2", stocked: false, name: "Maracuyá" },
        { id:uuidv4(), category: "Verduras", cantidad: "2", stocked: true, name: "Espinaca" },
        { id:uuidv4(), category: "Verduras", cantidad: "4", stocked: false, name: "Calabaza" },
        { id:uuidv4(), category: "Verduras", cantidad: "1", stocked: true, name: "Guisantes" }
      ]
      setCosas(products)
    }
  },[])

  useEffect(()=>{
    console.log(cosasupdt)
  }, [cosas])

  const addItem = (item) => {
    setCosas([...cosas, item])
  }
  const updateLocalStorage = (item) =>{
    setCosasupdt([...cosasupdt, item])

    let data = [...cosas, ]
    // localStorage.setItem('datos', data)
    console.log("updated")
    console.log(cosasupdt)
  }

  const deleteItem = (id) => {
    setCosas(cosas.filter(item => item.id !== id))
  } 
  return (
    <div>
      <h1>LISTA COMPRA</h1>
      <Formulario addItem={addItem} updateLocalStorage={updateLocalStorage} />
      <table>
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Cantidad</th>
            <th>Categoría</th>
          </tr>
        </thead>
        <tbody>
        {
        cosas.map(item => ( 
          <TableRow item={item} deleteItem={deleteItem} />
        ))
      }
        </tbody>
      </table>

    </div>
  )
}
