import React, { useEffect, useState } from 'react'
import { Formulario } from './components/Formulario'
import { v4 as uuidv4 } from 'uuid';
import { Tabla, TableRow } from './components/TableRow';
uuidv4();

export const App = () => {

  const products = [
    { id:uuidv4(), category: "Frutas", cantidad: "1", stocked: true, name: "Manzana" },
    { id:uuidv4(), category: "Frutas", cantidad: "1", stocked: true, name: "Fruta del dragón" },
    { id:uuidv4(), category: "Frutas", cantidad: "2", stocked: false, name: "Maracuyá" },
    { id:uuidv4(), category: "Verduras", cantidad: "2", stocked: true, name: "Espinaca" },
    { id:uuidv4(), category: "Verduras", cantidad: "4", stocked: false, name: "Calabaza" },
    { id:uuidv4(), category: "Verduras", cantidad: "1", stocked: true, name: "Guisantes" }
  ]
  let datos = JSON.stringify(products)
  localStorage.setItem('datos', datos)
  const [cosas, setCosas] = useState(products)
  useEffect(() => {
    const datos = localStorage.getItem('datos');
    if(datos){
      console.log(JSON.parse(datos))
    }
  },[])
  useEffect(() => {
    console.log("el use state ha cambiado")
    let newDatos = localStorage.stringify(cosas)
    // localStorage.setItem('datos', newDatos)
    console.log(newDatos)
  },[cosas])
  const addItem = (item) => {
    setCosas([...cosas, item])
  }

  const deleteItem = (id) => {
    setCosas(cosas.filter(item => item.id !== id))
  } 
  return (
    <div>
      <h1>LISTA COMPRA</h1>
      <Formulario addItem={addItem} />
      <table>
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Cantidad</th>
            <th>Categoría</th>
          </tr>
        </thead>
        <tbody>
        {
        cosas.map(item => (
          <TableRow item={item} deleteItem={deleteItem} />
        ))
      }
        </tbody>
      </table>

    </div>
  )
}
