import React, { useState } from 'react';

function FilterableProductTable({ products }) {
  const [filterText, setFilterText] = useState('');
  const [inStockOnly, setInStockOnly] = useState(false);

  return (
    <div>
      <SearchBar 
        filterText={filterText} 
        inStockOnly={inStockOnly} 
        onFilterTextChange={setFilterText} 
        onInStockOnlyChange={setInStockOnly} />
      <ProductTable 
        products={products} 
        filterText={filterText}
        inStockOnly={inStockOnly} />
    </div>
  );
}

function ProductCategoryRow({ category }) {
  return (
    <tr>
      <th colSpan="2">
        {category}
      </th>
    </tr>
  );
}

function ProductRow({ product, toggleCheck }) {
  const name = product.stocked ? product.name : <span>{product.name}</span>;

  const handleClick = (e) => {
    const trow = e.target.closest('tr');
    trow.classList.toggle('red-color');
    trow.classList.toggle('black-color');
    toggleCheck(product); // Llama a la función toggleCheck para marcar/desmarcar el producto
  };

  return (
    <tr onClick={handleClick}>
      <td>{name}</td>
      <td>{product.price}</td>
    </tr>
  );
}

function ProductTable({ products, filterText, inStockOnly, selectedRows, toggleCheck }) {
  const rows = [];
  let lastCategory = null;

  products.forEach((product) => {
    if (
      product.name.toLowerCase().indexOf(
        filterText.toLowerCase()
      ) === -1
    ) {
      return;
    }
    if (inStockOnly && !product.stocked) {
      return;
    }
    if (product.category !== lastCategory) {
      rows.push(
        <ProductCategoryRow
          category={product.category}
          key={product.category} />
      );
    }
    rows.push(
      <ProductRow
        product={product}
        key={product.name}
        toggleCheck={toggleCheck} />
    );
    lastCategory = product.category;
  });

  return (
    <table>
      <thead>
        <tr>
          <th>Nombre</th>
          <th>Precio</th>
        </tr>
      </thead>
      <tbody>{rows}</tbody>
    </table>
  );
}

function SearchBar({
  filterText,
  inStockOnly,
  onFilterTextChange,
  onInStockOnlyChange
}) {
  return (
    <form>
      <input 
        type="text" 
        value={filterText} placeholder="Buscar..." 
        onChange={(e) => onFilterTextChange(e.target.value)} />
      <label>
        <input 
          type="checkbox" 
          checked={inStockOnly} 
          onChange={(e) => onInStockOnlyChange(e.target.checked)} />
        {' '}
        Mostrar solo productos en stock
      </label>
    </form>
  );
}

const PRODUCTS = [
  {category: "Frutas", price: "$1", stocked: true, name: "Manzana"},
  {category: "Frutas", price: "$1", stocked: true, name: "Fruta del dragón"},
  {category: "Frutas", price: "$2", stocked: false, name: "Maracuyá"},
  {category: "Verduras", price: "$2", stocked: true, name: "Espinaca"},
  {category: "Verduras", price: "$4", stocked: false, name: "Calabaza"},
  {category: "Verduras", price: "$1", stocked: true, name: "Guisantes"}
];

export default function App() {
  const [selectedRows, setSelectedRows] = useState([]);

  const toggleCheck = (clickedItem) => {
    const updatedSelectedRows = selectedRows.includes(clickedItem.name)
      ? selectedRows.filter(row => row !== clickedItem.name)
      : [...selectedRows, clickedItem.name];
    setSelectedRows(updatedSelectedRows);
  };

  const deleteSelectedItems = () => {
    const updatedProducts = PRODUCTS.filter(product =>
      !selectedRows.includes(product.name)
    );
    console.log(updatedProducts); // Imprime los productos sin los seleccionados en la consola
    setSelectedRows([]); // Limpia el estado de las filas seleccionadas
  };

  return (
    <div>
      <FilterableProductTable products={PRODUCTS} selectedRows={selectedRows} toggleCheck={toggleCheck} />
      <button onClick={deleteSelectedItems}>Borrar seleccionados</button>
    </div>
  );
}
