import React, { useEffect, useState } from 'react'
import { Formulario } from './components/Formulario'
import { v4 as uuidv4 } from 'uuid';
import { Tabla, TableRow } from './components/TableRow';
uuidv4();

export const App = () => {


  const [cosas, setCosas] = useState([])
  //almacenar datos
  useEffect(() => {
    console.log("el use state ha cambiado")
    let nuevosDatos = JSON.stringify(cosas)
    localStorage.setItem('datos', nuevosDatos)
    console.log(localStorage.getItem('datos'))
  },[cosas])
  //leer datos
  useEffect(() => {
    const datos = localStorage.getItem('datos');
    if(datos){
      console.log(JSON.parse(datos))
    }
  },[])

  const addItem = (item) => {
    setCosas([...cosas, item])
  }

  const deleteItem = (id) => {
    setCosas(cosas.filter(item => item.id !== id))
  } 
  return (
    <div>
      <h1>LISTA COMPRA</h1>
      <Formulario addItem={addItem} />
      <table>
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Cantidad</th>
            <th>Categor√≠a</th>
          </tr>
        </thead>
        <tbody>
        {
        cosas.map(item => (
          <TableRow item={item} deleteItem={deleteItem} />
        ))
      }
        </tbody>
      </table>

    </div>
  )
}
