import React, { useEffect, useState } from 'react';
import { Formulario } from './components/Formulario';
import { v4 as uuidv4 } from 'uuid';
import { Tabla, TableRow } from './components/TableRow';

export const App = () => {
  // Verificar si hay datos almacenados en el almacenamiento local y obtenerlos
  const storedData = JSON.parse(localStorage.getItem('datos')) || [];
  const [cosas, setCosas] = useState(storedData);

  // Almacenar los datos actualizados en el almacenamiento local cada vez que el estado cambie
  useEffect(() => {
    localStorage.setItem('datos', JSON.stringify(cosas));
  }, [cosas]);


  const addItem = (item) => {
    setCosas([...cosas, item]);
  };

  const deleteItem = (id) => {
    setCosas(cosas.filter(item => item.id !== id));
  };

  return (
    <div>
      <h1>LISTA DE COMPRA</h1>
      <Formulario addItem={addItem} />
      <table>
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Cantidad</th>
            <th>Categor√≠a</th>
          </tr>
        </thead>
        <tbody>
          {cosas.map(item => (
            <TableRow key={item.id} item={item} deleteItem={deleteItem} />
          ))}
        </tbody>
      </table>
    </div>
  );
};
