import React, { useEffect, useState } from 'react';
import { Formulario } from './components/Formulario';
import { v4 as uuidv4 } from 'uuid';
import { Tabla, TableRow } from './components/TableRow';

export const App = () => {
  const [loadedFromLocalStorage, setLoadedFromLocalStorage] = useState(false);
  const [cosas, setCosas] = useState([]);

  useEffect(() => {
    const storedData = JSON.parse(localStorage.getItem('datos'));
    if (storedData) {
      setCosas(storedData);
      setLoadedFromLocalStorage(true);
    } else if (!loadedFromLocalStorage) {
      // Cargar datos predeterminados solo si no hay datos almacenados previamente
      cargarDatosPredeterminados();
    }
  }, []);

  useEffect(() => {
    // Almacenar los datos actualizados en el almacenamiento local cuando el estado cambie
    if (loadedFromLocalStorage) {
      localStorage.setItem('datos', JSON.stringify(cosas));
    }
  }, [cosas, loadedFromLocalStorage]);

  const cargarDatosPredeterminados = () => {
    const productosPredeterminados = [
      { id:uuidv4(), category: "Frutas", cantidad: "1", stocked: true, name: "Manzana" },
      { id:uuidv4(), category: "Frutas", cantidad: "1", stocked: true, name: "Fruta del dragón" },
      { id:uuidv4(), category: "Frutas", cantidad: "2", stocked: false, name: "Maracuyá" },
      { id:uuidv4(), category: "Verduras", cantidad: "2", stocked: true, name: "Espinaca" },
      { id:uuidv4(), category: "Verduras", cantidad: "4", stocked: false, name: "Calabaza" },
      { id:uuidv4(), category: "Verduras", cantidad: "1", stocked: true, name: "Guisantes" }
    ];
    setCosas(productosPredeterminados);
  };

  const addItem = (item) => {
    setCosas([...cosas, item]);
  };

  const deleteItem = (id) => {
    setCosas(cosas.filter(item => item.id !== id));
  };

  return (
    <div>
      <h1>LISTA DE COMPRA</h1>
      <Formulario addItem={addItem} />
      <table>
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Cantidad</th>
            <th>Categoría</th>
          </tr>
        </thead>
        <tbody>
          {cosas.map(item => (
            <TableRow key={item.id} item={item} deleteItem={deleteItem} />
          ))}
        </tbody>
      </table>
    </div>
  );
};
