import React, { useState } from 'react'
import { v4 as uuidv4 } from 'uuid';
uuidv4();

export const Formulario = ({addItem}) => {
  const [value, setValue] = useState("")
  const [num, setNum] = useState(1)
  const [category, setCategory] = useState("Otro")
  const handleChange = (e) => {
    e.preventDefault();
    let cosa = { id:uuidv4(), category: category, cantidad: num, stocked: true, name: value }
    addItem(cosa)
  }
  return (
      <form onSubmit={handleChange}>
        <div className='box-input'>
        <input className='form-control' type='text' placeholder='HOLA PACO' onChange={(e) => setValue(e.target.value)}/>
        <input className='form-control' type='number' min="1" value={num} onChange={(e) => setNum(e.target.value)}/> 
        </div>
        <select className="form-select" value={category} onChange={(e) => setCategory(e.target.value)}>
          <option value="Otro">Otro</option>
          <option value="Verduras" >Verduras</option>
          <option value="Frutas">Frutas</option>
          <option value="Carne">Carne</option>
          <option value="Pescado">Pescado</option>
        </select>
        <button type='submit' className='todo-btn'>submit</button>   
      </form>
  )
}
