import React, { useState } from 'react'
import { v4 as uuidv4 } from 'uuid';
uuidv4();

export const Formulario = ({addItem, filterText}) => {
  const [value, setValue] = useState("")
  const [num, setNum] = useState(1)
  const [category, setCategory] = useState("Otro")
  const handleChange = (e) => {
    e.preventDefault();
    let cosa = { id:uuidv4(), category: category, cantidad: num, stocked: true, name: value }
    addItem(cosa)
    setValue("")
  }
  return (
      <form onSubmit={handleChange}>
        <div class="row">
          <div class="col">
            <input class="form-control" required value={value} type="text" placeholder="AÃ±adir producto" onChange={(e) => setValue(e.target.value)} />
          </div>
          <div class="col">
            <input class="form-control" type="number" min="1" value={num} onChange={(e) => setNum(e.target.value)} />
          </div>
        </div>
        <br></br>
        <select className="form-select" value={category} onChange={(e) => setCategory(e.target.value)}>
          <option value="Otro">Otro</option>
          <option value="Verduras" >Verduras</option>
          <option value="Frutas">Frutas</option>
          <option value="Carne">Carne</option>
          <option value="Pescado">Pescado</option>
        </select>
        <br></br>
        <div className='row'>
          <button type='submit' className="btn btn-light">Submit <i class="bi bi-send"></i></button>
          <div className='col'></div>
        </div>   
      </form>
  )
}
