import React, { useState } from 'react';

function FilterableProductTable({ products }) {
  const [filterText, setFilterText] = useState('');
  const [inStockOnly, setInStockOnly] = useState(false);

  return (
    <div>
      <SearchBar 
        filterText={filterText} 
        inStockOnly={inStockOnly} 
        onFilterTextChange={setFilterText} 
        onInStockOnlyChange={setInStockOnly} />
      <ProductTable 
        products={products} 
        filterText={filterText}
        inStockOnly={inStockOnly} />
    </div>
  );
}

function ProductCategoryRow({ category }) {
  return (
    <tr>
      <th colSpan="2">
        {category}
      </th>
    </tr>
  );
}

function ProductRow({ product }) {
  const name = product.stocked ? product.name : <span>{product.name}</span>;
  const [itemfilt, setItemfilt] = useState( [])

  const handleClick = (e) => {
    const trow = e.target.closest('tr');
    trow.classList.toggle('red-color');
    trow.classList.toggle('black-color');
  };
  const selectItem= (id) => {
      setItemfilt(product.filter(item => item.id !== id))
  }

  return (
    <tr onClick={handleClick}>
      <td>{name}</td>
      <td>{product.price}</td>
      <td><input type="checkbox" onClick={selectItem(product.id)} /></td>
    </tr>
  );
}

function ProductTable({ products, filterText, inStockOnly }) {
  const rows = [];
  let lastCategory = null;

  products.forEach((product) => {
    if (
      product.name.toLowerCase().indexOf( 
        filterText.toLowerCase()
      ) === -1
    ) {
      return;
    }
    if (inStockOnly && !product.stocked) {
      return;
    }
    if (product.category !== lastCategory) {
      rows.push(
        <ProductCategoryRow
          category={product.category}
          key={product.category} />
      );
    }
    rows.push(
      <ProductRow
        product={product}
        key={product.name} />
    );
    lastCategory = product.category;
  });

  return (
    <table>
      <thead>
        <tr>
          <th>Nombre</th>
          <th>Cantidad</th>
        </tr>
      </thead>
      <tbody>{rows}</tbody>
    </table>
  );
}

function SearchBar({
  filterText,
  inStockOnly,
  onFilterTextChange,
  onInStockOnlyChange
}) {
  return (
    <form>
      <input 
        type="text" 
        value={filterText} placeholder="Buscar..." 
        onChange={(e) => onFilterTextChange(e.target.value)} />
      <label>
        <input 
          type="checkbox" 
          checked={inStockOnly} 
          onChange={(e) => onInStockOnlyChange(e.target.checked)} />
        {' '}
        Mostrar solo productos en stock
      </label>
    </form>
  );
}

const PRODUCTS = [
  {category: "Frutas", price: "1", stocked: true, name: "Manzana", chekc:false, id:1},
  {category: "Frutas", price: "1", stocked: true, name: "Fruta del drag칩n", chekc:false, id:2},
  {category: "Frutas", price: "2", stocked: false, name: "Maracuy치", chekc:false, id:3},
  {category: "Verduras", price: "2", stocked: true, name: "Espinaca", chekc:false, id:4},
  {category: "Verduras", price: "4", stocked: false, name: "Calabaza", chekc:false, id:5},
  {category: "Verduras", price: "1", stocked: true, name: "Guisantes", chekc:false, id:6}
];

export default function App() {
  const [items, setItems] = useState(PRODUCTS);

  const toggleCheck = (clickedItem) => {
    const updatedItems = items.map(item =>
      item === clickedItem ? { ...item, checked: !item.checked } : item
    );
